import React,{useState, CSSProperties} from 'react'
import { CSSTransition, SwitchTransition } from 'react-transition-group';
import styled from 'styled-components';
import Data from '../Data/LetterData';
import LoadingIcons from 'react-loading-icons'
import './Transition.css'
import {SyncLoader}from "react-spinners";
const Background = styled.div`
display: flex;
justify-content: center;
align-items: center;
flex-direction:column;
font-size: 60px;
width: 80%;
height: 80%;
`;
//white-space: pre;가 줄바꿈 허용 핵심
const Description = styled.div`
  background-color: #c6ebc5;
  width: 100%;
  height: 80%;
  display: flex;
  text-align:center;
  justify-content: center;
  align-items: center;
  font-size: 60px;
  white-space: pre;
  line-height:1.5em;
  color:#3D8361;
  font-family: var(--font-EF_WAKEUP);
  flex-direction:column;
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;

`;
const Description2 = styled.div`
  background-color: #c6ebc5;
  width: 100%;
  height: 100%;
  display: flex;
  text-align:center;
  justify-content: center;
  align-items: center;
  white-space: pre;
  line-height:1.5em;
  font-size: 45px;
  color:#3D8361;
  pagging:20%;
  font-family: var(--font-EF_WAKEUP);
  flex-direction:column;
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
  text-decoration: underline; 
  text-underline-position:under;
`;
const Present = styled.div`
  background-color: #c6ebc5;
  width: 100%;
  height: 80%;
  display: flex;
  text-align:center;
  justify-content: center;
  align-items: center;
  font-size: 120px;
  color:#3D8361;
  font-family: var(--font-EF_WAKEUP);
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
`;
const Title=styled.div`
  background-color: #fbf2cf;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 60px;
  width: 100%;
  height: 20%;
  color:#fa7070;
  font-family: var(--font-EF_WAKEUP);
  box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
`;
const ImageContainer = styled.img`
height:45%;
width:80%;
box-shadow: rgba(0, 0, 0, 0.35) 0px 5px 15px;
`
const override: CSSProperties = {
  display: "block",
  margin: "0 auto",

};

const LetterPage = ({page,incrNum}) => {
  const [load,setLoad] = useState(true)

  const increaseNumber = () =>{
    incrNum()
  }
  return (

    <SwitchTransition>
      <CSSTransition key={page} classNames="slide" timeout={500} appear>
        <Background>

          {
            page < 3 ? <Title>{Data.Titles[page]}</Title> : <div></div>
          }


          { 
            page < 3 ?
            (page === 0 ? 
            <Present onClick={increaseNumber}>
              {Data.Descriptions[page]}
            </Present>: <Description>

              {Data.Descriptions[page]}
            </Description>) : (page === 0 ? 
            <Present onClick={increaseNumber}>
              {Data.Descriptions[page]}
            </Present>: <Description2>
              {
                  Data.Photos[page] !== 0 ? 
                  load ? <SyncLoader 
                  size={20} color={"#3D8361"} loading={load}/>: 
                 <ImageContainer onLoad={()=>setLoad(false)} src={`/img/${page-1}.jpg`}>
                  </ImageContainer>:<div></div>

              }
              {Data.Descriptions[page]}
            </Description2>)

          }

        </Background>
        </CSSTransition>
    </SwitchTransition>


  )
}

export default LetterPage
