  background-color: #fbf2cf;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 60px;
  width: 100%;
  height: 20%;
  color:#fa7070;
  font-family: var(--font-EF_WAKEUP);
`;
const LetterPage = ({page}) => {
