import React from 'react'
import { CSSTransition, SwitchTransition } from 'react-transition-group';
import { LazyLoadImage } from 'react-lazy-load-image-component';
import styled from 'styled-components';
import Data from '../Data/LetterData';
import LoadingIcons from 'react-loading-icons'
import Photo from '../Images/photo.png'
import './Transition.css'
const Background = styled.div`
display: flex;
justify-content: center;
align-items: center;
flex-direction:column;
font-size: 60px;
width: 80%;
height: 80%;
`;
//white-space: pre;가 줄바꿈 허용 핵심
const Description = styled.div`
  background-color: #c6ebc5;
  width: 100%;
  height: 80%;
  display: flex;
  text-align:center;
  justify-content: center;
  align-items: center;
  font-size: 60px;
  white-space: pre;
  line-height:1.5em;
  color:#3D8361;
  font-family: var(--font-EF_WAKEUP);
  flex-direction:column;
`;
const Description2 = styled.div`
  background-color: #c6ebc5;
  width: 100%;
  height: 100%;
  display: flex;
  text-align:center;
  justify-content: center;
  align-items: center;
  white-space: pre;
  line-height:1.5em;
  font-size: 45px;
  color:#3D8361;
  pagging:20%;
  font-family: var(--font-EF_WAKEUP);
  flex-direction:column;
`;
const Present = styled.div`
  background-color: #c6ebc5;
  width: 100%;
  height: 80%;
  display: flex;
  text-align:center;
  justify-content: center;
  align-items: center;
  font-size: 120px;
  color:#3D8361;
  font-family: var(--font-EF_WAKEUP);
`;
const Title=styled.div`
  background-color: #fbf2cf;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 60px;
  width: 100%;
  height: 20%;
  color:#fa7070;
  font-family: var(--font-EF_WAKEUP);
`;

const LetterPage = ({page,incrNum}) => {


  const increaseNumber = () =>{
    incrNum()
  }
  return (

    <SwitchTransition>
      <CSSTransition key={page} classNames="slide" timeout={500} appear>
        <Background>

          {
            page < 3 ? <Title>{Data.Titles[page]}</Title> : <div></div>
          }


          { 
            page < 3 ?
            (page === 0 ? 
            <Present onClick={increaseNumber}>
              {Data.Descriptions[page]}
            </Present>: <Description>

              {Data.Descriptions[page]}
            </Description>) : (page === 0 ? 
            <Present onClick={increaseNumber}>
              {Data.Descriptions[page]}
            </Present>: <Description2>
              {
                  Data.Photos[page] !== 0 ? 

                 <LazyLoadImage
                  height={"45%"}
                  src={Photo} // use normal <img> attributes as props
                  width={"80%"} 
                  effect="blur"/>:<div></div>

              }
              {Data.Descriptions[page]}
            </Description2>)

          }

        </Background>
        </CSSTransition>
    </SwitchTransition>


  )
}

export default LetterPage
