import styled from 'styled-components';
import React from "react";
import ReactDOM from "react-dom";
import { BrowserRouter as Router } from 'react-router-dom';
import { HashRouter, Switch, Link, Route, Redirect } from "react-router-dom";
import { TransitionGroup, CSSTransition } from "react-transition-group";

import "./Transition.css"
const Background = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 100vh;
  flex-direction: column;
  background-color : #fa7070;
`;

const InnerBox = styled.div`
  background-color: #c6ebc5;
  width: 80%;
  height: 80%;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 60px;
  color:#3D8361;
  font-family: var(--font-EF_WAKEUP);
`;
const Box2=styled.div`
  background-color: #fbf2cf;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 60px;
  width: 80%;
  height: 10%;
  color:#fa7070;
  font-family: var(--font-EF_WAKEUP);
`;

const Container = () => {
  return (
    <Background>
        <React.Fragment>

            <Box2>2021.10.29</Box2>
                <InnerBox>
            D+365
            <br/>
            Our First
            <br/>
            Anniversary
            <br/>
            ❤
            </InnerBox>
            <Route
            render={({ location }) => (
            <TransitionGroup>
                <CSSTransition
                key={location.pathname}
                classNames="fade"
                timeout={600}
                >
                <Switch location={location}>
                    <Route
                    path="/penguin"
                    render={() => (
                        <Link className="link" to="/lion">
                        🐧→🦁
                        </Link>
                    )}
                    />
                    <Route
                    path="/lion"
                    render={() => (
                        <Link className="link" to="/chimp">
                        🦁→🐵
                        </Link>
                    )}
                    />
                    <Route
                    path="/chimp"
                    render={() => (
                        <Link className="link" to="/finch">
                        🐵→🐤
                        </Link>
                    )}
                    />
                    <Route
                    path="/finch"
                    render={() => (
                        <Link className="link" to="/bee">
                        🐤→🐝
                        </Link>
                    )}
                    />
                    <Route
                    path="/bee"
                    render={() => (
                        <Link className="link" to="/penguin">
                        🐝→🐧
                        </Link>
                    )}
                    />
                    <Route
                    path="/"
                    render={() => (
                        <Link className="link white" to="/penguin">
                        <img
                            src="https://pbs.twimg.com/profile_images/702439715270729728/vMmnId56_400x400.jpg"
                            width="96px"
                            height="96px"
                        />
                        </Link>
                    )}
                    />
                </Switch>
                </CSSTransition>
            </TransitionGroup>
            )}
        />


        </React.Fragment>
    </Background>
  )
}

export default Container
