import React, { useEffect } from 'react'
import { TransitionGroup,CSSTransition } from 'react-transition-group';
import styled from 'styled-components';
import Data from '../Data/LetterData';
const Background = styled.div`
display: flex;
justify-content: center;
align-items: center;
flex-direction:column;
font-size: 60px;
width: 80%;
height: 80%;
`;
const Description = styled.div`
  background-color: #c6ebc5;
  width: 100%;
  height: 80%;
  display: flex;
  text-align:center;
  justify-content: center;
  align-items: center;
  font-size: 60px;
  color:#3D8361;
  font-family: var(--font-EF_WAKEUP);
`;
const Present = styled.div`
  background-color: #c6ebc5;
  width: 100%;
  height: 80%;
  display: flex;
  text-align:center;
  justify-content: center;
  align-items: center;
  font-size: 120px;
  color:#3D8361;
  font-family: var(--font-EF_WAKEUP);
`;
const Title=styled.div`
  background-color: #fbf2cf;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 60px;
  width: 100%;
  height: 20%;
  color:#fa7070;
  font-family: var(--font-EF_WAKEUP);
`;
const LetterPage = ({page,incrNum}) => {
  useEffect(()=>{
    console.log(page)
  },[page])
  const increaseNumber = () =>{
    incrNum()
  }
  return (
    <TransitionGroup>
      <CSSTransition key={page} classNames="slide" timeout={500}>
        <Background>
          <Title>{Data.Titles[page]}</Title>
              {
                page == 0 ? 
                <Present onClick={increaseNumber}>
                  {Data.Descriptions[page]}
                </Present>: <Description>
                  {Data.Descriptions[page]}
                </Description>
              }

        </Background>
      </CSSTransition>
    </TransitionGroup>

  )
}

export default LetterPage
